entity.deletedPkQuery:
select 'spending:' || agency_id || ':' || disbursement_line_item_id id from disbursement_line_item_deleted where job_id >= ${dataimporter.request.jobID}
                                UNION
                                select 'spending:' || agency_id || ':' || disbursement_line_item_id id from subcontract_spending_deleted where job_id >= ${dataimporter.request.jobID}

select 'contracts:'|| agreement_id id from agreement_snapshot_deleted where job_id >= ${dataimporter.request.jobID}
                                UNION
                                select 'contracts:'|| agreement_id id from sub_agreement_snapshot_deleted where job_id >= ${dataimporter.request.jobID}
