widgetDataLoader:
    $where_filters = array();
    foreach($node->widgetConfig->requestParams as $param=>$value){
        $where_filters[] = _widget_build_sql_condition(' a1.' . $param, $value);
    }

    if(count($where_filters) > 0){
        $where_filter = ' where ' . implode(' and ' , $where_filters);
    }

 	$where_filter .= ' and rd.document_code in (' . ContractUtil::getCurrentPageDocumentIds() . ') ';
    if(preg_match('/pending/',$_GET['q'])){
    	$sql = ' select
			revised_maximum_amount maximum_contract_amount,
			a1.document_agency_name agency_name,
			fms_contract_number contract_number,
			contract_number pending_contract_number,
			vendor_legal_name legal_name,
			document_code,
			original_master_agreement_id original_agreement_id,
			version_number
		from {pending_contracts} a1
    	join {vendor} v on v.vendor_id =  a1.vendor_id
    	join {ref_agency} ra on ra.agency_id =  a1.document_agency_id
    	join {ref_document_code} rd on a1.document_code_id = rd.document_code_id
   		' . $where_filter . '
    	and revised_maximum_amount != 0
        order by 1 desc limit 10';
    }else{
    	$sql = ' select
			maximum_contract_amount,
			agency_name,
			contract_number,
			legal_name,
			document_code,
			original_agreement_id
		from {aggregateon_mwbe_contracts_cumulative_spending} a1
    	join {vendor} v on v.vendor_id =  a1.vendor_id
    	join {ref_agency} ra on ra.agency_id =  a1.agency_id
    	join {ref_document_code} rd on a1.document_code_id = rd.document_code_id
   		' . $where_filter . '
        and maximum_contract_amount != 0
    	order by 1 desc limit 10';

    }

    $node->data  = _checkbook_project_execute_sql($sql);

widgetUpdateJSONConfig:
	$series_new = array();


	$index = 0;
	foreach($node->data as $row){
	 		$tooltip_label =
	 						'Contract ID: ' . $row['contract_number'].  '<br/>' .
	 						'Amount: ' . custom_number_formatter_format($row['maximum_contract_amount'] ,2, '$').  '<br/>' .
	 						'Prime Vendor: ' . $row['legal_name'] .  '<br/>' .
	 						'Contracting Agency: ' . $row['agency_name'] .  '<br/>'
	 						;

	 		if(preg_match('/pending/',$_GET['q'])){

	 			$link_url = _pending_contracts_link_contract_details($row['pending_contract_number'], $row['original_agreement_id'], $row['document_code'],
	 							$row['contract_number'] , $row['version_number'], 'bar');


	 			//$link_url = strstr($_GET['q'],'/',true) . _checkbook_project_get_url_param_string('status')  .  _checkbook_project_get_year_url_param_string() . _checkbook_append_url_params() .
				//				 '?expandBottomContURL=/minipanels/pending_contract_transactions/contract/' .  $row['pending_contract_number'] . '/version/' . $row['version_number'];
	 		}else{
				$link_url = strstr($_GET['q'],'/',true) . _checkbook_project_get_url_param_string('status')  .  _checkbook_project_get_year_url_param_string() . _checkbook_append_url_params() .
								 '?expandBottomContURL=/panel_html/contract_transactions'
                        . ( in_array($row['document_code'], array('MMA1','MA1')) ? '/contract_details' : '' )
                        . ( in_array($row['document_code'], array('MMA1','MA1')) ? '/magid/' : '/agid/' )
                        .  $row['original_agreement_id'] . '/doctype/' . $row['document_code']
                        . _checkbook_append_url_params()	 ;
            }
	 		$series_new[$index]->url = $link_url;
			$series_new[$index]->y = (float)$row['maximum_contract_amount'];
			$series_new[$index]->tooltip_label = $tooltip_label;
			$node->widgetConfig->gridConfig->data[] = array($row['contract_number'],$row['maximum_contract_amount'],$row['legal_name'], $row['agency_name']  );
			$index += 1;
	}

	$node->widgetConfig->chartConfig->series[0]->name = 'Fiscal Years';
	$node->widgetConfig->chartConfig->series[0]->type = 'column';
	$node->widgetConfig->chartConfig->series[0]->pointWidth = 30;
	$node->widgetConfig->chartConfig->series[0]->data = $series_new;
	if(RequestUtilities::getRequestParamValue('status') == 'A'){
		$status = 'Active';
	}else if(RequestUtilities::getRequestParamValue('status') == 'R'){
		$status = 'Registered';
	}

	$contract_type = 'Expense';
	if(preg_match('/revenue/',$_GET['q'])){
		$contract_type = 'Revenue';
	}
	if(preg_match('/pending_exp/',$_GET['q'])){
		$contract_type = 'Pending Expense';
	}
	if(preg_match('/pending_rev/',$_GET['q'])){
		$contract_type = 'Pending Revenue';
	}
	$mwbe_filter = RequestUtilities::getRequestParamValue('mwbe');
	if($mwbe_filter =='4~5' || $mwbe_filter =='2' || $mwbe_filter =='3' || $mwbe_filter =='9' ){
		$node->widgetConfig->chartTitle = MappingUtil::getCurrenEthnicityName() . ' Top Ten ' . $status . ' ' . $contract_type .  ' Contracts  by Current Amount';
	}else{
		$node->widgetConfig->chartTitle = 'Top Ten ' . MappingUtil::getCurrenEthnicityName() . ' ' . $status . ' ' . $contract_type .  ' Contracts  by Current Amount';
	}
