widgetDataLoader:

    $where_filters = array();
    foreach($node->widgetConfig->requestParams as $param=>$value){
        $where_filters[] = _widget_build_sql_condition(' a1.' . $param, $value);
    }

    if(count($where_filters) > 0){
        $where_filter = ' where ' . implode(' and ' , $where_filters);
    }

    $where_filter .= ' and rd.document_code in (\'CT1\',\'CTA1\') ';

    $sql = ' SELECT
		COUNT(DISTINCT a1.contract_number) total_contracts,
                SUM(CASE WHEN a1.scntrc_status = 1 THEN 1 ELSE 0 END) no_response_status,
                SUM(CASE WHEN a1.scntrc_status = 2 THEN 1 ELSE 0 END) reported_sub_count,
                SUM(CASE WHEN a1.scntrc_status = 3 THEN 1 ELSE 0 END) reported_no_sub_count
                FROM {aggregateon_mwbe_contracts_cumulative_spending} a1 
                JOIN {ref_document_code} rd ON a1.document_code_id = rd.document_code_id '
                . $where_filter;
    $node->data  = _checkbook_project_execute_sql($sql);

widgetUpdateJSONConfig:

	$series_data = array();
        foreach($node->data as $row){
            $total_contracts = $row['total_contracts'];
            $series_data[] = array('status' => 'Contracts With No Response by Prime', 'count' => $row['no_response_status']);
            $series_data[] = array('status' => 'Reported Contracts With Sub Vendors', 'count' => $row['reported_sub_count']);
            $series_data[] = array('status' => 'Reported Contracts Without Sub Vendors', 'count' => $row['reported_no_sub_count']);
        }
	$index = 0;
	foreach($series_data as $row){	 								 						
            $series_new[$index]->y = (int)$row['count'];
            $series_new[$index]->name = $row['status'];
            $index += 1;
	}
	$node->widgetConfig->chartConfig->series[0]->data = $series_new;
	$node->widgetConfig->chartTitle =  'Sub Vendor Reporting by Prime Contracts';
        $node->widgetConfig->chartSubTitle =  'Total Number of Contracts: '.number_format($total_contracts);
