        //fallback for error
        magid = (magid == null || magid.length == 0) ?  '0' : magid;

        var sUrl = '/dashboard_platform/data_tables/ajax_data/node/'+ (mastercode == 'MA1' ? '509' : '271') +'/magid/'+magid+'/contstatus/A';
