adjustParameters:
	checkbook_faceted_search_adjust_param($node);
    if(function_exists('_checkbook_project_adjustContractParameterFilters')){
        _checkbook_project_adjustContractParameterFilters($node,$parameters,'active_registered','agreement_snapshot');
    }
    foreach($parameters as $key=>$value){
       if($key == 'document_code@checkbook:ref_document_code' || $key == 'document_code.document_code.document_code'){
           foreach($value as $a=>$b){
               $value[$a] = _checkbook_project_get_name_for_argument('document_code',$b);
           }
           $parameters['agreement_snapshot.agreement_snapshot.document_code_id'] = $value;
           unset($parameters[$key]);
       }
    }
    if(function_exists('_checkbook_project_applyParameterFilters')){
        $adjustedParameters = _checkbook_project_applyParameterFilters($node,$parameters);
		$adjustedParameters['vendor_id.vendor_id'][] = data_controller_get_operator_factory_instance()->initiateHandler(NotEmptyOperatorHandler::$OPERATOR__NAME);
        return $adjustedParameters;
    }
    return $parameters;
