adjustParameters:
	checkbook_faceted_search_adjust_param($node);
    if(function_exists('_checkbook_project_adjustContractParameterFilters')){
        _checkbook_project_adjustContractParameterFilters($node,$parameters,'active_registered');
    }
    if(RequestUtilities::getRequestParamValue('vendor') || RequestUtilities::getRequestParamValue('vendornm')){
        $parameters['is_vendor_flag'] = 'Y';
    }
    if(!(RequestUtilities::getRequestParamValue('year') || RequestUtilities::getRequestParamValue('calyear'))){
        $parameters['latest_flag'] = 'Y';
        $parameters['if_for_all_years'] = 'Y';
    }

    //For Master agreement details page vendor facet should only display Master agreement Prime vendors
    if(isset($parameters['document_code.document_code.document_code']) && count(array_intersect($parameters['document_code.document_code.document_code'], array('MA1', 'MMA1')))){
        $parameters['is_vendor_flag'] = 'N';
        if(isset($parameters['vendor.vendor'])){
            $parameters['display_vendor.display_vendor'] = $parameters['vendor.vendor'];
            unset($parameters['vendor.vendor']);
        }
        if(isset($parameters['vendor_id'])){
            $parameters['display_vendor_id'] = $parameters['vendor_id'];
            unset($parameters['vendor_id']);
        }
    }
    if(function_exists('_checkbook_project_applyParameterFilters')){
        $adjustedParameters = _checkbook_project_applyParameterFilters($node,$parameters);
        $adjustedParameters['vendor.vendor'][] = data_controller_get_operator_factory_instance()->initiateHandler(NotEmptyOperatorHandler::$OPERATOR__NAME);
        //log_error($adjustedParameters);
        return $adjustedParameters;
    }
    return $parameters;
