adjustParameters:
$nid = _getRequestParamValue('smnid');
    if(!isset($nid)) {
         $nid = !isset($nid) ? _getRequestParamValue('dtsmnid') : $nid;
    }
    if(isset($nid)){
        switch($nid){
            case 325:
            case 323:
             $node->widgetConfig->dataset = 'checkbook:payroll_latest_employee_agency';
            break;
            default:
            break;
        }
    }
    $monthId = _getRequestParamValue('month');
    if(isset($monthId)){
        $node->widgetConfig->dataset = 'checkbook:payroll_employee_agency_month';
    }
    if(function_exists('_checkbook_project_applyParameterFilters')){
        $adjustedParameters = _checkbook_project_applyParameterFilters($node,$parameters);
    }
    $adjustedParameters['civil_service_title.civil_service_title'][] = data_controller_get_operator_factory_instance()->initiateHandler(NotEmptyOperatorHandler::$OPERATOR__NAME);
    return $adjustedParameters;
