widgetDataLoader:
$where_filters = array();
foreach($node->widgetConfig->requestParams as $param=>$value){
$where_filters[] = _widget_build_sql_condition( $param, $value);
}

if(count($where_filters) > 0){
$where_filter = ' where ' . implode(' and ' , $where_filters);
}


$datasource = RequestUtilities::getRequestParamValue('datasource');
$sql = ' select
vendor_name,vendor_id
, sum(original_amount) current_amount_sum
, count(purchase_order_number) total_contracts
from contracts_fy_po_vendor_summary_vw
' . $where_filter . '
group by vendor_name  ,vendor_id
order by 3 desc limit 10';

$node->data  = _checkbook_project_execute_sql($sql,'main',$datasource);

widgetUpdateJSONConfig:
$series_new = array();


$index = 0;
foreach($node->data as $row){
$tooltip_label =
'Vendor: ' . $row['vendor_name'] .  '<br/>' .
'Number of Contracts: ' . $row['total_contracts'].  '<br/>' .
'Amount: ' . custom_number_formatter_format($row['current_amount_sum'] ,2, '$').  '<br/>'
;
//$link= CustomURLHelper::prepareUrl(strstr($_GET['q'],'/',true), ContractURLHelper::$landingPageParams, array('expandBottomCont'=>'true'),
//			array('vendor'=>$row['vendor_id']) );

$link = strstr($_GET['q'],'/',true) .  ContractUtil::get_contracts_vendor_link($row['vendor_id'],RequestUtilities::getRequestParamValue('year'),
RequestUtilities::getRequestParamValue('yeartype'), RequestUtilities::getRequestParamValue('agency')) .
'?expandBottomCont=true';
$series_new[$index]->url = 	$link;

$series_new[$index]->y = (float)$row['current_amount_sum'];
$series_new[$index]->tooltip_label = $tooltip_label;
$node->widgetConfig->gridConfig->data[] = array($row['vendor_name'],$row['total_contracts'],$row['current_amount_sum'] );
$index += 1;
}

$node->widgetConfig->chartConfig->series[0]->name = 'Fiscal Years';
$node->widgetConfig->chartConfig->series[0]->type = 'column';
$node->widgetConfig->chartConfig->series[0]->pointWidth = 30;
$node->widgetConfig->chartConfig->series[0]->data = $series_new;
