widgetDataLoader:
  $where_filters = [];
  foreach($node->widgetConfig->requestParams as $param=>$value){
    if($param == 'fiscal_year_id'){
      $where_filters[] = '('. $value . ' BETWEEN y.year_id AND y1.year_id)';
    }else{
      $where_filters[] = _widget_build_sql_condition( $param, $value);
    }
  }
  $where_filters[] = _widget_build_sql_condition( 'agreement_type_code', 'BA');

  $where_filter = '';
  if(count($where_filters) > 0){
    $where_filter = ' WHERE ' . implode(' AND ' , $where_filters);
  }


$datasource = RequestUtilities::getRequestParamValue('datasource');
$sql = 'SELECT
    contract_id,
    purpose,
    vendor_name,
    total_amount,
    original_amount,
    spend_to_date,
    amount_difference AS dollar_difference,
    percent_difference
  FROM contract_agreements_fy_vendor c
JOIN ref_year y ON c.start_year = y.year_value
JOIN ref_year y1 ON c.end_year = y1.year_value
' . $where_filter . '
ORDER BY total_amount DESC LIMIT 10';

LogHelper::log_error($sql);
$node->data  = _checkbook_project_execute_sql($sql,'main',$datasource);

widgetUpdateJSONConfig:
$series_new = [];


$index = 0;
foreach($node->data as $row){
$tooltip_label =
'Contract ID: ' . $row['contract_id'] .  '<br/>' .
'Vendor: ' . $row['vendor_name'] .  '<br/>' .
'Contracting Agency: NYCHA <br />' .
'Amount: ' . custom_number_formatter_format($row['total_amount'] ,2, '$').  '<br/>'
;

$link = strstr($_GET['q'],'/',true) . NychaContractsUrlService::contractDetailsUrl($row['contract_id']);
$series_new[$index]->url = 	$link;

$series_new[$index]->y = (float)$row['total_amount'];
$series_new[$index]->tooltip_label = $tooltip_label;
$node->widgetConfig->gridConfig->data[] = array($row['contract_id'],$row['vendor_name'],'NYCHA',$row['total_amount'] );
$index += 1;
}

$node->widgetConfig->chartConfig->series[0]->name = 'Fiscal Years';
$node->widgetConfig->chartConfig->series[0]->type = 'column';
$node->widgetConfig->chartConfig->series[0]->pointWidth = 30;
$node->widgetConfig->chartConfig->series[0]->data = $series_new;
