  $landing_page =  '/' .  current_path()  ;
  $allZero = TRUE;
  foreach ($node->data as $row) {
  if (abs($row['revenue_recognized_amount']) > 0){$allZero = FALSE;}
if (abs($row['revenue_funds_available']) > 0){$allZero = FALSE;}

$url = $landing_page .  '/expcategory/' . $row['expenditure_type_id_expenditure_type_id'];

$categories[] = html_entity_decode($row['expenditure_type_description_expenditure_type_description'],ENT_QUOTES);
$recognized_data[] = array('name' => html_entity_decode($row['expenditure_type_description_expenditure_type_description'],ENT_QUOTES), 'y' => $row['revenue_recognized_amount'], 'url' => $url);
$remaining_data[] = array('name' => html_entity_decode($row['expenditure_type_description_expenditure_type_description'],ENT_QUOTES), 'y' => $row['revenue_funds_available'], 'url' => $url);
}
if($allZero){$node->totalDataCount = 0;}
$node->widgetConfig->chartTitle = 'Top Ten Expense Categories by Revenue';
$node->widgetConfig->chartConfig->xAxis->categories = $categories;

$node->widgetConfig->chartConfig->series = array(array('name' => 'Recognized', 'data' => $recognized_data),array('name' => 'Remaining', 'data' => $remaining_data));
return $node->data;
