$year = _getYearValueFromID($parameters['fiscal_year_id'][0]);
if($parameters['type_of_year'][0] == 'B'){
    $SeriesCurrentYearLabel = 'FY '   .  $year;
    $SeriesPreviousYearLabel = 'FY '.  ($year - 1);
}else{
    $SeriesCurrentYearLabel = 'CY '   .  $year;
    $SeriesPreviousYearLabel = 'CY '.  ($year - 1);
}
$node->widgetConfig->chartConfig->series[0]->name = $SeriesPreviousYearLabel;
$node->widgetConfig->chartConfig->series[1]->name = $SeriesCurrentYearLabel;

foreach($node->data as $key=>$value){
    if($value['current_total_gross_pay'] == 0)
        $value['current_total_gross_pay'] = null;
    if($value['previous_total_gross_pay'] == 0)
        $value['previous_total_gross_pay'] =null;
}

$categories=array();
$allZero = true;

$isAgencyPage = (strpos(current_path(),'payroll/agency_landing/') === 0);
$pathParams = explode('/',drupal_get_path_alias(current_path()));
$data_source= RequestUtilities::_getUrlParamString('datasource');

foreach ($node->data as $row){
    $categories[]=html_entity_decode($row['month_month_month_name'],ENT_QUOTES);

    $currentYearUrl = current_path() . '?expandBottomContURL=' . ($isAgencyPage
        ? '/panel_html/payroll_agency_by_month_transactions/payroll/agencywide/monthly/transactions' . _checkbook_project_get_year_url_param_string() .$data_source. _checkbook_project_get_url_param_string('agency') . _checkbook_project_get_url_param_string('title') . '/month/' . $row['current_month_month']
        : '/panel_html/payroll_by_month_nyc_transactions/payroll/monthly/transactions' . _checkbook_project_get_year_url_param_string() .$data_source. _checkbook_project_get_url_param_string('agency') . _checkbook_project_get_url_param_string('title') . '/month/' . $row['current_month_month']);

    $previousYearUrl = current_path() . '?expandBottomContURL=' . ($isAgencyPage
        ? '/panel_html/payroll_agency_by_month_transactions/payroll/agencywide/monthly/transactions' . _checkbook_project_get_year_url_param_string(false, true) .$data_source. _checkbook_project_get_url_param_string('agency') . _checkbook_project_get_url_param_string('title') . '/month/' . $row['previous_month_month']
        : '/panel_html/payroll_by_month_nyc_transactions/payroll/monthly/transactions' . _checkbook_project_get_year_url_param_string(false, true) .$data_source. _checkbook_project_get_url_param_string('agency') . _checkbook_project_get_url_param_string('title') . '/month/' . $row['previous_month_month']);

    if(abs($row['previous_total_gross_pay']) > 0)
        $allZero = false;

    if(abs($row['current_total_gross_pay']) > 0)
        $allZero = false;

    $seriesPreviousData[] = (object)array( 'y' => $row['previous_total_gross_pay'], 'url'=> $previousYearUrl);
    $seriesCurrentData[] = (object)array('y' => $row['current_total_gross_pay'],'url'=> $currentYearUrl);
}

if($allZero) $node->totalDataCount = 0;
$node->widgetConfig->chartConfig->series[0]->data = $seriesPreviousData;
$node->widgetConfig->chartConfig->series[1]->data = $seriesCurrentData;
$node->widgetConfig->chartConfig->xAxis->categories = $categories;
$node->widgetConfig->chartTitle = 'Gross Pay by Month';
if($year == 2010){
    foreach($node->data as $row){
        $row['previous_total_gross_pay'] = null;
    }
    $node->widgetConfig->chartConfig->series[0]->showInLegend = false;
    $node->widgetConfig->chartConfig->series[0]->visible = false;
    unset($node->widgetConfig->gridConfig->table_columns[1]);
}
return $node->data;
