	 			$series[$row['year_value']]->y += $row['total_spending'];
	 		$series[$row['year_value']]->mwbe_total += $row['total_spending'];
	 		break;
	 		case '4':
	 		if(in_array(4,$mwbe_page_cats))
	 			$series[$row['year_value']]->y += $row['total_spending'];
	 		$series[$row['year_value']]->mwbe_total += $row['total_spending'];
	 		break;
	 		case '5':
	 		if(in_array(5,$mwbe_page_cats))
	 			$series[$row['year_value']]->y += $row['total_spending'];
	 		$series[$row['year_value']]->mwbe_total += $row['total_spending'];
	 		break;
	 		case '9':
