	 	}
	 }

	$i = 0;
	$categories = array();
	ksort($series);

	$series_new = array();
	$index = 0;

	foreach($series as $key=>$row){
		if($key <= $year && $key >= $year - 4){
			if(RequestUtilities::getRequestParamValue('yeartype') == 'C'){
				 $year_display = 'CY ' . $key;
			}else{
				$year_display = 'FY ' . $key;
			}
	 		$tooltip_label =
	 						'Year: ' . $year_display.  '<br/>' .
	 						'Amount: '. custom_number_formatter_format($series[$key]->y,2,'$')   . '<br/>' ;

			$series_new[$index]->y = $series[$key]->y/($series[$key]->mwbe_total + $non_mwbe_data[$key] ) * 100;
			$series_new[$index]->tooltip_label = $tooltip_label;
			$series_new[$index]->custom_label = custom_number_formatter_format($series[$key]->y/($series[$key]->mwbe_total + $non_mwbe_data[$key] ) *100,1,null,'%') ;
			$categories[] = $key ;
			$node->widgetConfig->gridConfig->data[] = array($year_display,$series[$key]->y );
			$index += 1;
		}
	}

    $categoryId = RequestUtilities::getRequestParamValue('category');
    if(isset($categoryId)){
        $categoryDetails = SpendingUtil::getSpendingCategoryDetails($categoryId,'display_name');
        if(is_array($categoryDetails)){
            $spend_cat = $categoryDetails[0]['display_name'];
            $spend_cat = str_replace('Spending', 'Prime Spending', $spend_cat);
        }
    }
    else $spend_cat = 'Total Prime Spending';
	$node->widgetConfig->chartTitle = MappingUtil::getCurrenEthnicityName() . ' ' . $spend_cat . ' Share';
