widgetDataLoader:
    $where_filters = array();
    foreach($node->widgetConfig->requestParams as $param=>$value){
        $where_filters[] = _widget_build_sql_condition(' a1.' . $param, $value);
    }
    $mwbe_filter = RequestUtilities::getRequestParamValue('mwbe');
    $where_filters_mwbe = $where_filters;
    $where_filters_mwbe[] = _widget_build_sql_condition('minority_type_id', $mwbe_filter);

    if(count($where_filters) > 0){
        $where_filter = ' where ' . implode(' and ' , $where_filters);
    }
    if(count($where_filters_mwbe) > 0){
        $where_filters_mwbe = ' where ' . implode(' and ' , $where_filters_mwbe);
    }

    $sort_column = 8;
    switch($mwbe_filter){
        case '4~5' :
        case '4' :
        case '5' :
            $sort_column = 3;
            $ethinicty_case = ' WHEN minority_type_id = 4 THEN total_spending_amount
            WHEN minority_type_id = 5 THEN total_spending_amount ';
        break;
        case '2' :
            $sort_column = 4;
            $ethinicty_case = ' WHEN minority_type_id = 2 THEN total_spending_amount
            ';
        break;
        case '9' :
            $sort_column = 5;
            $ethinicty_case = ' WHEN minority_type_id = 9 THEN total_spending_amount
            ';
        break;
            case '3' :
            $sort_column = 6;
            $ethinicty_case = ' WHEN minority_type_id = 3 THEN total_spending_amount
            ';
        break;
            case '11' :
            $sort_column = 7;
            $ethinicty_case = ' WHEN minority_type_id = 11 THEN total_spending_amount
            ';
        break;
            case '7' :
            $sort_column = 9;
            $ethinicty_case = ' WHEN minority_type_id = 7 THEN total_spending_amount
            ';
        break;
            case '2~3~4~5~9' :
            $sort_column = 9;
            $ethinicty_case = ' WHEN minority_type_id = 2 THEN total_spending_amount
            WHEN minority_type_id = 3 THEN total_spending_amount
            WHEN minority_type_id = 4 THEN total_spending_amount
            WHEN minority_type_id = 5 THEN total_spending_amount
            WHEN minority_type_id = 9 THEN total_spending_amount
            ';
        break;

    }

    $sql = 'SELECT X.agency_name, X.agency_id, X.ethinicty_mwbe, Y.total_mwbe, Y.total_non_mwbe FROM';

    $sql .= '(select  agency_name , a1.agency_id,
    SUM(
    CASE
    ' . $ethinicty_case . '
    ELSE 0
    END) ethinicty_mwbe
    from {aggregateon_subven_spending_coa_entities} a1
    join {ref_agency} ra on ra.agency_id =  a1.agency_id
    ' . $where_filters_mwbe . '
    group by  agency_name,a1.agency_id) X';

    $sql .= ' JOIN ';

    $sql .= '(select  agency_name , a1.agency_id,
    SUM(
    CASE WHEN minority_type_id = 2 THEN total_spending_amount
    WHEN minority_type_id = 3 THEN total_spending_amount
    WHEN minority_type_id = 4 THEN total_spending_amount
    WHEN minority_type_id = 5 THEN total_spending_amount
    WHEN minority_type_id = 9 THEN total_spending_amount
    ELSE 0
    END) total_mwbe,
    SUM(
    CASE
    WHEN minority_type_id = 7 THEN total_spending_amount
    ELSE 0
    END) total_non_mwbe
    from {aggregateon_subven_spending_coa_entities} a1
    join {ref_agency} ra on ra.agency_id =  a1.agency_id
    ' . $where_filter . '
    group by  agency_name,a1.agency_id) Y';

    $sql .= ' ON X.agency_id = Y.agency_id ORDER BY X.ethinicty_mwbe desc';

    $node->data  = _checkbook_project_execute_sql($sql);
	$node->data_merged =  $node->data;

widgetUpdateJSONConfig:
	$series_new = array();


	$mwbe_filter = RequestUtilities::getRequestParamValue('mwbe');
	$amount_column = $mwbe_filter != null ? 'ethinicty_mwbe' : 'total_mwbe';

	$mwbe_filter = RequestUtilities::getRequestParamValue('mwbe');
	if($mwbe_filter =='4~5' || $mwbe_filter =='4' || $mwbe_filter =='5' || $mwbe_filter =='2' || $mwbe_filter =='3' || $mwbe_filter =='9' ){
		$node->widgetConfig->chartTitle = MappingUtil::getCurrenEthnicityName()  . ' Sub Spending';

		$node->widgetConfig->chartTitle = MappingUtil::getCurrenEthnicityName()  . ' Top Ten Agencies Sub Spending';
	}else{
		$node->widgetConfig->chartTitle = 'Top Ten Agencies by ' . MappingUtil::getCurrenEthnicityName() .' Sub Spending';

	}

	$index = 0;
	foreach($node->data_merged as $row){
        $tooltip_label =
        'Agency: ' . $row['agency_name'].  '<br/>' .
        MappingUtil::getCurrenEthnicityName() . ' Share: ' . custom_number_formatter_format($row[$amount_column]/($row['total_mwbe'] + $row['total_non_mwbe'] ) *100 ,1,null, '%').  '<br/>' .
        MappingUtil::getCurrenEthnicityName() . ' Spending: ' . custom_number_formatter_format( $row[$amount_column],2,'$').  '<br/>' .
        'Non-M/WBE: ' . custom_number_formatter_format( $row['total_non_mwbe']  ,2,'$').  '<br/>';

        $node->widgetConfig->gridConfig->data[] = array($row['agency_name'],
        custom_number_formatter_format($row[$amount_column]/($row['total_mwbe'] + $row['total_non_mwbe'] ) *100 ,1,null, '%'),
        (float)$row[$amount_column] ,
        (float)$row['total_non_mwbe']);

        $link= CustomURLHelper::prepareUrl('spending_landing', SpendingUtil::$landingPageParams, array('expandBottomCont'=>'true'), array('agency'=>$row['agency_id']) );
			$series_new[$index]->url = $link;

			$series_new[$index]->y = (float)$row[$amount_column];
			$series_new[$index]->tooltip_label = $tooltip_label;

			$index += 1;
			if($index == 10){
				break;
			}
	}

	$node->widgetConfig->chartConfig->series[0]->name = 'Fiscal Years';
	$node->widgetConfig->chartConfig->series[0]->type = 'column';
	$node->widgetConfig->chartConfig->series[0]->pointWidth = 30;
	$node->widgetConfig->chartConfig->series[0]->data = $series_new;
