widgetDataLoader:

    $where_filters = array();
    foreach($node->widgetConfig->requestParams as $param=>$value){
        $where_filters[] = _widget_build_sql_condition(' a1.' . $param, $value);
    }

    if(count($where_filters) > 0){
        $where_filter = ' where ' . implode(' and ' , $where_filters);
    }

	$mwbe_filter = _getRequestParamValue('mwbe');
    $sort_column = 8;
	switch($mwbe_filter){
		case '4~5' :
			$sort_column = 3;
			break;
		case '2' :
			$sort_column = 4;
			break;
		case '9' :
			$sort_column = 5;
			break;
		case '3' :
			$sort_column = 6;
			break;
		case '11' :
			$sort_column = 7;
			break;
		case '7' :
			$sort_column = 9;
			break;

	}

$sql = '
select legal_name, vendor_id, prime_minority_type_id, total_spending_amount 

from (

    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 4 THEN total_spending_amount WHEN prime_minority_type_id = 5 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 



    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 2 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 



    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 9 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 



    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 3 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 



    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 11 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 



    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 2 THEN total_spending_amount WHEN prime_minority_type_id = 3 THEN total_spending_amount WHEN prime_minority_type_id = 4 THEN total_spending_amount WHEN prime_minority_type_id = 5 THEN total_spending_amount WHEN prime_minority_type_id = 9 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 


    UNION 



    select legal_name, a1.prime_vendor_id vendor_id,prime_minority_type_id, 

    SUM( CASE WHEN prime_minority_type_id = 7 THEN total_spending_amount ELSE 0 END) total_spending_amount 

    from aggregateon_subven_spending_coa_entities a1 join vendor ra on ra.vendor_id = a1.prime_vendor_id 

    ' . $where_filter . ' 

    group by legal_name, a1.prime_vendor_id,a1.prime_minority_type_id 

) all_spending where all_spending.total_spending_amount > 0 

order by all_spending.total_spending_amount desc limit 10 

';
//log_error('SQQQLLL: 


' . $sql);

    $node->data  = _checkbook_project_execute_sql($sql);

widgetUpdateJSONConfig:
	$series_new = array();

    $mwbe_filter = '';
    foreach($node->data as $row){
        $pos = strpos($mwbe_filter,$row['prime_minority_type_id']);
        if($pos === false) {
            $mwbe_filter .= $mwbe_filter == '' ? $row['prime_minority_type_id'] : '~'.$row['prime_minority_type_id'];
        }
    }

	$amount_column = 'total_mwbe';
	switch($mwbe_filter){
		case '4~5' :
			$amount_column = 'aa_mwbe';
			break;
		case '2' :
			$amount_column = 'ba_mwbe';
			break;
		case '3' :
			$amount_column = 'ha_mwbe';
			break;
		case '9' :
			$amount_column = 'w_mwbe';
			break;
		case '11' :
			$amount_column = 'io_nonmwbe';
			break;
		case '7' :
			$amount_column = 'total_non_mwbe';
			break;
	}

    $amount_column = 'total_spending_amount';

	$mwbe_filter = _getRequestParamValue('mwbe');
	if($mwbe_filter =='4~5' || $mwbe_filter =='2' || $mwbe_filter =='3' || $mwbe_filter =='9' ){
		$node->widgetConfig->chartTitle = MappingUtil::getCurrenEhtnicityName()  . ' Top Ten Prime Vendors Sub Spending';
	}else{
		$node->widgetConfig->chartTitle = 'Top Ten Prime Vendors by ' . MappingUtil::getCurrenEhtnicityName() .' Sub Spending';
	}
	$index = 0;
	foreach($node->data as $row){

        $tooltip_label =
            'Prime Vendor: ' . $row['legal_name'].  '<br/>' .
            MappingUtil::getMinorityCategoryById($row['prime_minority_type_id']) . ' Spending: ' . custom_number_formatter_format( $row[$amount_column],2,'$');
        $node->widgetConfig->gridConfig->data[] = array(
            $row['legal_name'],
            $row[$amount_column]
        );

        $link = substr(SpendingUtil::getPrimeVendorNameLinkUrl($node, $row),1);
        $series_new[$index]->url = $link;


        $series_new[$index]->y = (float)$row[$amount_column];
        $series_new[$index]->tooltip_label = $tooltip_label;
        $index += 1;
	}

	$node->widgetConfig->chartConfig->series[0]->name = 'Fiscal Years';
	$node->widgetConfig->chartConfig->series[0]->type = 'column';
	$node->widgetConfig->chartConfig->series[0]->pointWidth = 30;
	$node->widgetConfig->chartConfig->series[0]->data = $series_new;
