statement.sql:
SELECT s0.type_of_year, s0.year_id AS year_year, s0.agency_id AS agency_agency, a.agency_name, SUM(total_spending_amount) AS check_amount_sum, SUM(total_spending_amount)/MAX(total.check_amount)*100 as percent_spending FROM aggregateon_subven_spending_coa_entities s0 JOIN ref_agency a ON a.agency_id = s0.agency_id JOIN ( SELECT SUM(total_spending_amount) AS check_amount FROM aggregateon_subven_spending_coa_entities
) total on 1 = 1
GROUP BY s0.agency_id, s0.year_id, s0.type_of_year, a.agency_name
